#!/usr/bin/env node
const argv = require('yargs')
    .option('port', {
        alias: 'p',
        describe: 'Specify the port number',
        default: 7860
    })
    .option('uuid', {
        alias: 'u',
        describe: 'Specify the uuid',
        default: '35d48aba-a0e4-4032-8c08-6185b9bcffa8'
    })
    .help()
    .argv;

// 通过参数启动你的应用
require('./index.js')(argv.port, argv.uuid);
